async function validateLicense(){const e=window.ACCESSIBILITY_CONFIG;if(!e||!e.licenseKey)return console.error("Accessibility Widget: License key is required"),!1;try{const i=await fetch("https://wcag-g2za.onrender.com/api/licenses/verify",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({licenseKey:e.licenseKey,domain:e.domain||window.location.hostname})});return(await i.json()).valid?(trackUsage(),!0):(console.error("Accessibility Widget: Invalid license key"),!1)}catch(e){return console.error("Accessibility Widget: License validation failed",e),!1}}async function trackUsage(){try{await fetch("https://wcag-g2za.onrender.com/api/usage/track",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({licenseKey:window.ACCESSIBILITY_CONFIG.licenseKey,domain:window.ACCESSIBILITY_CONFIG.domain||window.location.hostname})})}catch(e){console.error("Accessibility Widget: Usage tracking failed",e)}}async function init(){if(document.getElementById("accessibility-widget"))return;await validateLicense()||console.error("Accessibility Widget: License validation failed. Widget will not initialize.")}